INSERT INTO drinks(idDrink,strDrink,strDrinkAlternate,strDrinkES,strDrinkDE,strDrinkFR,strDrinkZHHANS,strDrinkZHHANT,strTags,strVideo,strCategory,strIBA,strAlcoholic,strGlass,strInstructions,strInstructionsES,strInstructionsDE,strInstructionsFR,strInstructionsZHHANS,strInstructionsZHHANT,strDrinkThumb,strIngredient1,strIngredient2,strIngredient3,strIngredient4,strIngredient5,strIngredient6,strIngredient7,strIngredient8,strIngredient9,strIngredient10,strIngredient11,strIngredient12,strIngredient13,strIngredient14,strIngredient15,strMeasure1,strMeasure2,strMeasure3,strMeasure4,strMeasure5,strMeasure6,strMeasure7,strMeasure8,strMeasure9,strMeasure10,strMeasure11,strMeasure12,strMeasure13,strMeasure14,strMeasure15,strImageSource,strImageAttribution,strCreativeCommonsConfirmed,dateModified) VALUES (13024,'Sweet Sangria',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Punch / Party Drink',NULL,'Alcoholic','Pitcher','Dissolve the sugar in hot water and cool. Peel the citrus fruits and break into wedges. Mix the wine, sugar syrup, fruit, and Fresca in a pitcher and put in the fridge for a few hours. Serve in tall glasses with a straw.',NULL,'Den Zucker in heißem Wasser auflösen und abkühlen lassen. Die Zitrusfrüchte schälen und stückeln. Wein, Zuckersirup, Obst und Fresca in einem Krug mischen und für einige Stunden in den Kühlschrank stellen. In hohen Gläsern mit einem Strohhalm servieren.',NULL,NULL,NULL,'https://www.thecocktaildb.com/images/media/drink/uqqvsp1468924228.jpg','Red wine','Sugar','Water','Apple','Orange','Lime','Lemon','Fresca',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'2 bottles','1 cup','2 cups hot','1 cup','wedges','wedges',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'No','2016-07-19 11:30:28');
